mapper.update:
 
		UPDATE MAP
			SET
			map_latitude = #{map_latitude},
			map_longitude = #{map_longitude}
			WHERE SPACE_NO=#{space_no}

mapper.select:
select * from (select * from (select * from
(select * from
space
(select * from space where space_name like '%'||#{search}||'%' or space_tag like '%'||#{search}||'%' or space_addr1 like '%'||#{search}||'%' or space_category like '%'||#{search}||'%')
NATURAL JOIN(select space_no from space where space_name like '%'||#{search}||'%' or space_tag like '%'||#{search}||'%' or space_addr1 like '%'||#{search}||'%' or space_category like '%'||#{search}||'%')
space_addr1 like '%'||#{SpaceDTO.space_addr1}||'%'
)
space_category = #{SpaceDTO.space_category}
)space
fac_no like '%'||#{item}||',%'
and fac_no like '%,'||#{item}||'%'
)

select s.space_no, avg(r.rev_rate) rev_rate,space_name, count(r.mem_no) count,count(DISTINCT(FAV_NO)) favorite, s.mem_no , s.space_category,space_addr1,space_addr2, s.SPACE_INTRO1,s.SPACE_IMG1,m.mem_img from SPACE s FULL OUTER join review r on r.space_no =s.space_no FULL OUTER join MEMBER m on s.mem_no = m.mem_no FULL OUTER join FAVORITE f on s.space_no = f.space_no
s.space_no = #{space_no}
GROUP BY s.space_no, s.mem_no, s.space_category,space_addr1,space_addr2, s.SPACE_INTRO1,s.SPACE_IMG1,m.mem_img,s.space_name having s.space_no is not null order by space_no desc
